<?phpclass TegresosModel extends ModelBase {      function getTodosTegresos() {     $query = "select 			fac_tegresos.ID_TEGRESO,                 fac_tegresos.NOMBRE_TEGRESO                from fac_tegresos" ;                $consulta = $this->consulta($query);               return $consulta;           }      function getDatosTegreso($ID_TEGRESO) {            $query = "select 			fac_tegresos.ID_TEGRESO,                 fac_tegresos.NOMBRE_TEGRESO                from fac_tegresos                      where fac_tegresos.ID_TEGRESO='".$ID_TEGRESO."'";         $consulta = $this->consulta($query);        return $consulta[0];        }    function insertarTegreso($NOMBRE_TEGRESO) {         $query = "INSERT INTO fac_tegresos (NOMBRE_TEGRESO)		VALUES('".utf8_decode(mb_strtoupper($NOMBRE_TEGRESO))."');";               return $this->crear_ultimo_id($query);           }    function editarTegreso($ID_TEGRESO, $NOMBRE_TEGRESO) {       $query = "UPDATE fac_tegresos  SET NOMBRE_TEGRESO = '".utf8_decode(mb_strtoupper($NOMBRE_TEGRESO))."'        WHERE ID_TEGRESO = '" . $ID_TEGRESO . "'";       return $this->modificarRegistros($query);    }    function eliminarTegreso($ID_TEGRESO) {        $query = "DELETE FROM fac_tegresos WHERE ID_TEGRESO = '". $ID_TEGRESO ."'";        $this->modificarRegistros($query);    }}?>