<script type="text/javascript" >function buscar_estudiante(texto) {        if(texto.length < 3) {        $('#vista_estudiantes').hide();    } else {        ejecutarAccion("reportes", "Reportes", "buscarEstudiante", "texto="+texto, "$('#vista_estudiantes').show(); $('#vista_estudiantes').html(data);");    }}function buscar_facturas(id_estudiante) {            ejecutarAccion("reportes", "Reportes", "SalidaFacturasPorEstudiante", "id_estudiante="+id_estudiante, "$('#salida_facturas_estudiantes').show(); $('#salida_facturas_estudiantes').html(data);");    }function seleccionar_estudiante(ID, NOMBRES, APELLIDOS, DOCUMENTO){    var texto = NOMBRES+' '+APELLIDOS+' (DOCUMENTO: '+DOCUMENTO+')';        $("#text_estudiante").val(texto);      $('#vista_estudiantes').hide();        buscar_facturas(ID);}		</script><?php$froms = new Formularios();?><div class="box box-default" style="padding-left: 10%; padding-right: 10%">      <a name="arriba"></a>    <div class="box-header with-border">                <h3 class="box-title">Reporte de Facturas por Estudiante</h3>    </div>        <div class="box-body">    <div class="row">                                  <div class="col-md-12">            <div class="form-group">                 <label>Seleccionar Estudiante: </label>                <input id="text_estudiante" name="text_estudiante" autocomplete ="off" class="form-control" style="height: 35px" type="text" onkeyup="buscar_estudiante(this.value); return false;"  />                <div id="vista_estudiantes">                </div>         </div>      </div>    </div>            <div id="salida_facturas_estudiantes">    </div>        </div></div>