<script type="text/javascript" >function buscar_grupo(texto) {        if(texto.length < 3) {        $('#vista_grupos').hide();    } else {        ejecutarAccion("reportes", "Reportes", "buscarGrupoVencidas", "texto="+texto, "$('#vista_grupos').show(); $('#vista_grupos').html(data);");    }}function salida_facturas_vencidas() {           ejecutarAccion("reportes", "Reportes", "SalidaReportePorGrupoVencidas", "id_grupo="+$("#grupo").val()+"&estado="+$("#estado").val(), "$('#salida_facturas').show(); $('#salida_facturas').html(data);");    }function seleccionar_grupo_vencidas(ID, CODIGO, PROGRAMA, ANO, SEMESTRE){    var texto = CODIGO+' '+PROGRAMA+' ('+ANO+' - '+SEMESTRE+')';        $("#grupo").val(ID);      $("#text_grupo").val(texto);      $('#vista_grupos').hide();        salida_facturas_vencidas();}	function buscar_facturas_vencidas(id_estudiante) {            ejecutarAccion("reportes", "Reportes", "SalidaFacturasVencidas", "id_estudiante="+id_estudiante, "$('#salida_facturas_vencidas').show(); $('#salida_facturas_vencidas').html(data);");    }</script><?php$froms = new Formularios();?><div class="box box-default" style="padding-left: 10%; padding-right: 10%">      <a name="arriba"></a>    <div class="box-header with-border">                <h3 class="box-title">Reporte por Facturas Vencidas</h3>    </div>        <div class="box-body">    <div class="row">                                                  <div class="col-md-12">            <div class="form-group">                 <label>Seleccionar el estado de los Estudiantes: </label>                             <select onchange="salida_facturas_vencidas(); return false;" class="form-control"  name="estado" id="estado">                     <option value="TODOS">TODOS</option>                     <option value="1">ACTIVO</option>                     <option value="7">EN ETAPA PRODUCTIVA</option>                     <option value="3">GR