<script type="text/javascript" >function buscar_grupo(texto) {        if(texto.length < 3) {        $('#vista_grupos').hide();    } else {        ejecutarAccion("reportes", "Reportes", "buscarGrupo", "texto="+texto, "$('#vista_grupos').show(); $('#vista_grupos').html(data);");    }}function salida_factura(id_grupo) {            ejecutarAccion("reportes", "Reportes", "SalidaReportePorGrupo", "id_grupo="+id_grupo, "$('#salida_facturas').show(); $('#salida_facturas').html(data);");    }function seleccionar_grupo(ID, CODIGO, PROGRAMA, ANO, SEMESTRE){    var texto = CODIGO+' '+PROGRAMA+' ('+ANO+' - '+SEMESTRE+')';        $("#text_grupo").val(texto);      $('#vista_grupos').hide();        salida_factura(ID);}	</script><?php$froms = new Formularios();?><div class="box box-default" style="padding-left: 10%; padding-right: 10%">      <a name="arriba"></a>    <div class="box-header with-border">                <h3 class="box-title">Reporte por Grupo</h3>    </div>        <div class="box-body">    <div class="row">                                  <div class="col-md-12">            <div class="form-group">                 <label>Seleccionar Grupo: </label>                <input id="grupo" name="grupo" type="hidden" />                <input id="text_grupo" name="text_grupo" autocomplete ="off" class="form-control" style="height: 35px" type="text" onkeyup="buscar_grupo(this.value); return false;"  />                <div id="vista_grupos">                </div>         </div>      </div>    </div>            <div id="salida_facturas">    </div>            </div></div>